import React, { useState, useEffect } from "react";
import { GoogleMap, useLoadScript, Marker } from "@react-google-maps/api";

const libraries = ["places"];
const mapContainerStyle = {
  width: "100%",
  height: "100%",
};
const center = {
  lat: 49.246292, // Vancouver latitude
  lng: -123.11934, // Vancouver longitude
};

const Map = ({
  activePlayer,
  positions,
  setPositions,
  targetPosition,
  mapMode,
}) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  // Log updated positions whenever the state changes
  useEffect(() => {
    console.log("Updated positions:", positions);
  }, [positions]);

  const handleMapClick = (event) => {
    if (mapMode === "input" && activePlayer) {
      const lat = event.latLng.lat();
      const lng = event.latLng.lng();

      setPositions((prevPositions) => {
        const updatedPositions = [...prevPositions];
        updatedPositions[activePlayer - 1] = { lat, lng }; // Update the active player's position
        return updatedPositions;
      });

      console.log(`Player ${activePlayer} dropped a pin at:`, { lat, lng });
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;

  return (
    <GoogleMap
      mapContainerStyle={mapContainerStyle}
      zoom={12}
      center={center}
      onClick={handleMapClick}
    >
      {/* Render markers for Player 1 and Player 2 */}
      {positions[0].lat && (
        <Marker
          position={positions[0]}
          icon="http://maps.google.com/mapfiles/ms/icons/red-dot.png"
        />
      )}
      {positions[1].lat && (
        <Marker
          position={positions[1]}
          icon="http://maps.google.com/mapfiles/ms/icons/blue-dot.png"
        />
      )}

      {/* Render the target marker in result mode */}
      {mapMode === "result" && (
        <Marker
          position={targetPosition}
          icon="http://maps.google.com/mapfiles/ms/icons/green-dot.png"
        />
      )}
    </GoogleMap>
  );
};

export default Map;
