import React, { useState } from "react";
import "./App.css";
import Map from "./Map";
import placeholder1 from "./place-holder.jpg";
import placeholder2 from "./place-holder.jpg";


function App() {
  const [currentScreen, setCurrentScreen] = useState("start");
  const [userOneGuesses, setUserOneGusses] = useState(null);
  const [currentGuess, setCurrentGuess] = useState({
    lat: 49.2628,
    lng: -123.0995,
  }); // placeholder for mount pleaset

  const handleNavigation = (nextState) => {
    setCurrentScreen(nextState);
  };

  const handleSubmit = () => {
    setUserOneGusses({ lat: 49.2606, lng: -123.246 }); // placeholder, UBC
    setCurrentScreen("result");
  };

  const handleNextMural = () => {
    setCurrentScreen("input");
    setUserOneGusses(null); // placeholder for now 
    handleNavigation("input");
  };

  const handleEndGame = () => {
    setCurrentScreen("end");
  };

  console.log("Current Screen:", currentScreen);

  return (
    <div className="container">
      <div className="side-panel">
        <div className="content">
          {currentScreen === "start" && (
            <>
              <h1>Welcome to the Mural Game!</h1>
              <p>
                {" "}
                Guess the locations of beauitful murals around the City of
                Vancouver!
              </p>
              <button onClick={() => handleNavigation("input")}>
                Start Game
              </button>
            </>
          )}
          {currentScreen === "input" && (
            <>
              <h1>Round 1 </h1>
              <p>
                {" "}
                On the map, pleaes drop the pin at where you think the mural is
                located, then click the submit button.📍
              </p>
              <img src={placeholder1} alt="placeholder" />
              <button onClick={handleSubmit}>Submit</button>
            </>
          )}
          {currentScreen === "result" && (
            <>
              <h1>Round 1 result </h1>
              <p> How close did you get?! Please see the map for results.</p>
              <img src={placeholder1} alt="placeholder" />
              <div className="mural-description">
                <p> About this mural </p>
                <p>
                  {" "}
                  2021 - Vancouver - The Present is a Gift Wandering the streets
                  of Vancouver's Mount Pleasant neighbourhood. The S.W. corner
                  of Main Street & Broadway Avenue is being prepared for a
                  Skytrain extension station. Tearing down the corner lot
                  buildings has offered improved street level exposure to this
                  mural from Vancouver's 2016 Mural Festival. Hopefully the new
                  station will not hide the work again. Artists Drew Young & Jay
                  Senetchko painted the mural - "The Present is a Gift" on the
                  north walls of Belvedere Court apartment building. The
                  “Present is a Gift” is a play on words to bring awareness to
                  the present moment, to live in the now. The portraits
                  reference two Mount Pleasant residents. PaisleyNahanee (left
                  side) is a Coast-Salish First Nations who was born and grew up
                  in Mount Pleasant. & Dr. Bob has worked at an area optometrist
                  office for over 6 decades. As two longstanding residents,
                  Paisley and Bob capture the essence, history and culture of
                  Mount Pleasant.
                </p>
              </div>
              <div className="button-container">
                <button className="side-by-side"> Next mural</button>{" "}
                <button className="side-by-side"> End game </button>
              </div>
            </>
          )}
          {currentScreen === "end" && (
            <>
              <h1>This is end screen. </h1>
              <p>
                {" "}
                On the map, pleaes drop the pin at where you think the mural is
                located, then click the submit button.📍
              </p>
              <img src={placeholder1} alt="placeholder" />
              <button> Restart game </button>
            </>
          )}
        </div>
      </div>
      <div className="map-container">
        <Map />
      </div>
    </div>
  );
}

export default App;
